# Load modified paternity function

AgamapaternityIndex <-
function (dataframe, marker = "marker", father = "father", off = "off", Afreq = "a", Bfreq = "b", afcalcol =  NA) {
 fathern = father
 offn = off
 if (Afreq == "calculate" | Bfreq == "calculate") {
 df1 <- t(dataframe[, afcalcol])
 
allefreqA <- function (x) {
 aft <- table (x)
AAfreq = aft["AA"] 
ABfreq =  aft ["AB"]
BBfreq =   aft ["BB"]
AAfreq[is.na (AAfreq)]<-  0
ABfreq[is.na (ABfreq)]<-  0
BBfreq[is.na (BBfreq)]<-  0
totalfq = AAfreq + BBfreq + ABfreq 
afreq =  (AAfreq  + 0.5* ABfreq) / totalfq
return (afreq) 
}
mark_A_freq <- apply(df1,2, allefreqA)

 allefreqB <- function (x) {
aft <- table (x)
AAfreq = aft["AA"]
ABfreq =  aft ["AB"]
BBfreq =   aft ["BB"]
AAfreq[is.na (AAfreq)]<-  0
ABfreq[is.na (ABfreq)]<-  0
BBfreq[is.na (BBfreq)]<-  0
totalfq = AAfreq + BBfreq + ABfreq 
bfreq =  (BBfreq + 0.5* ABfreq) / totalfq
return (bfreq)
}
mark_B_freq <- apply(df1,2, allefreqB)

alldata = data.frame(marker = dataframe [, marker], father = dataframe [, father],
 off = dataframe[,off], a = mark_A_freq,
  b=mark_B_freq, stringsAsFactors=FALSE)
} else {
# dataframe tp be applied to
alldata = data.frame(marker = dataframe [, marker], father = dataframe [, father], off = dataframe[,off], 
 a = dataframe [, Afreq], b=dataframe [, Bfreq], stringsAsFactors=FALSE)
}
 # conditions dataframe
 #1     2      3      4       5      6     7      8  
 father <-   c("AA",  "BB", "BB",  "AB",   "AB",  "AA", "AB",  "AA")
 off <-      c("AA",  "BB",  "AB", "BB",   "AA",  "AB",  "AB", "BB")
 Formula <- c("1/a",	"1/b", "1/(2*b)", "1/(2*b)",	"1/(2*a)",  "1/(2*a)",   "((a+b)/(4/(a*b)))",  0)
 COEFA <-    c(1,    0,      0,     0,     0.5,     0.5,     0,   0)
 COEFAB <-   c(0,    0,      0,     0,     0,     0,     1,    0)
 COEFB <-    c(0,    1,      0.5,     0.5,     0,     0,     0,    0)
 coefft <- data.frame (father, off, Formula, COEFA, COEFAB, COEFB)
 
 # create conditions data frame
 cond = data.frame(MTR_CLD_AF = paste(father,off,sep="~"), COEFA, COEFAB,COEFB, stringsAsFactors=FALSE)
 
 # geting coeffients for each row in alldata
 matcond=cond[match(with(alldata, paste(father,off, sep="~")),
                    cond$MTR_CLD_AF),]
 alldata$COEFA = matcond$COEFA
 alldata$COEFAB = matcond$COEFAB
 alldata$COEFB = matcond$COEFB
 
 alldata$PI = with(alldata, COEFA*(1/a) +  COEFB*(1/b) + COEFAB*((a+b)/(4/(a*b))))
 out<- summary(alldata$PI == 0)
 results <- data.frame (marker = alldata$marker, PI = alldata$PI)
 return(results)
}


